@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
The @apply directive should not be used with the group utility class.
*/

@layer base {
  :root {

  }

  .dark {

  }
}

input.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Fresh, health-focused color palette */
    --background: 150 30% 98%;
    --foreground: 160 25% 12%;

    /* Primary - Fresh Emerald Green */
    --primary: 158 64% 42%;
    --primary-foreground: 0 0% 100%;

    /* Secondary - Soft Sage */
    --secondary: 150 20% 94%;
    --secondary-foreground: 158 40% 30%;

    /* Muted */
    --muted: 150 15% 92%;
    --muted-foreground: 160 15% 45%;

    /* Accent - Light mint */
    --accent: 158 50% 92%;
    --accent-foreground: 158 60% 30%;

    /* Card */
    --card: 0 0% 100%;
    --card-foreground: 160 25% 12%;

    /* Popover */
    --popover: 0 0% 100%;
    --popover-foreground: 160 25% 12%;

    /* Border */
    --border: 150 20% 88%;
    --input: 150 20% 88%;
    --ring: 158 64% 42%;

    /* Destructive / Warning for unhealthy ingredients */
    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;

    /* Warning - Orange for caution */
    --warning: 38 92% 50%;
    --warning-foreground: 0 0% 100%;

    /* Success - for healthy items */
    --success: 158 64% 42%;
    --success-foreground: 0 0% 100%;

    --radius: 1rem;

    /* Gradients */
    --hero-gradient: linear-gradient(135deg, hsl(158 64% 42% / 0.1) 0%, hsl(180 50% 95%) 50%, hsl(150 40% 96%) 100%);
    --card-gradient: linear-gradient(180deg, hsl(0 0% 100%) 0%, hsl(150 30% 98%) 100%);
    --glow-gradient: radial-gradient(circle, hsl(158 64% 42% / 0.2) 0%, transparent 70%);

    /* Sidebar */
    --sidebar-background: 0 0% 100%;
    --sidebar-foreground: 160 25% 12%;
    --sidebar-primary: 158 64% 42%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 158 50% 92%;
    --sidebar-accent-foreground: 158 60% 30%;
    --sidebar-border: 150 20% 88%;
    --sidebar-ring: 158 64% 42%;
  }

  .dark {
    --background: 160 20% 8%;
    --foreground: 150 20% 95%;

    --primary: 158 64% 50%;
    --primary-foreground: 160 25% 8%;

    --secondary: 160 15% 15%;
    --secondary-foreground: 150 20% 90%;

    --muted: 160 15% 18%;
    --muted-foreground: 150 15% 60%;

    --accent: 158 40% 20%;
    --accent-foreground: 158 60% 80%;

    --card: 160 18% 12%;
    --card-foreground: 150 20% 95%;

    --popover: 160 18% 12%;
    --popover-foreground: 150 20% 95%;

    --border: 160 15% 20%;
    --input: 160 15% 20%;
    --ring: 158 64% 50%;

    --destructive: 0 62% 55%;
    --destructive-foreground: 0 0% 100%;

    --warning: 38 85% 55%;
    --warning-foreground: 0 0% 100%;

    --success: 158 64% 50%;
    --success-foreground: 160 25% 8%;

    --hero-gradient: linear-gradient(135deg, hsl(158 64% 20% / 0.3) 0%, hsl(160 20% 10%) 50%, hsl(160 15% 8%) 100%);
    --card-gradient: linear-gradient(180deg, hsl(160 18% 14%) 0%, hsl(160 18% 10%) 100%);
    --glow-gradient: radial-gradient(circle, hsl(158 64% 50% / 0.15) 0%, transparent 70%);

    --sidebar-background: 160 18% 10%;
    --sidebar-foreground: 150 20% 95%;
    --sidebar-primary: 158 64% 50%;
    --sidebar-primary-foreground: 160 25% 8%;
    --sidebar-accent: 158 40% 20%;
    --sidebar-accent-foreground: 158 60% 80%;
    --sidebar-border: 160 15% 20%;
    --sidebar-ring: 158 64% 50%;
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  /* Glass effect */
  .glass {
    @apply bg-card/80 backdrop-blur-xl border border-border/50 shadow-lg;
  }

  /* Gradient text */
  .gradient-text {
    @apply bg-gradient-to-r from-primary via-primary/80 to-primary bg-clip-text text-transparent;
  }

  /* Scan animation container */
  .scan-container {
    @apply relative overflow-hidden rounded-2xl;
  }

  .scan-container::after {
    content: '';
    @apply absolute inset-x-0 h-1 bg-gradient-to-r from-transparent via-primary to-transparent;
    animation: scan-line 2s ease-in-out infinite;
  }

  /* Health score badge variants */
  .health-badge {
    @apply inline-flex items-center gap-1.5 px-3 py-1 rounded-full text-sm font-medium;
  }

  .health-badge-good {
    @apply bg-success/15 text-success;
  }

  .health-badge-warning {
    @apply bg-warning/15 text-warning;
  }

  .health-badge-bad {
    @apply bg-destructive/15 text-destructive;
  }

  /* Floating animation delay utilities */
  .float-delay-1 { animation-delay: 0.5s; }
  .float-delay-2 { animation-delay: 1s; }
  .float-delay-3 { animation-delay: 1.5s; }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

