import { Eye, Languages, AlertTriangle, Lock, Zap, Heart } from "lucide-react";

const features = [
  {
    icon: Eye,
    title: "Reveal Hidden Sugars",
    description: "Uncover the 56+ names manufacturers use to disguise sugar in ingredients lists.",
    color: "destructive" as const,
  },
  {
    icon: Languages,
    title: "Translate Any Label",
    description: "Scan products from any country. Emma translates and explains every ingredient.",
    color: "primary" as const,
  },
  {
    icon: AlertTriangle,
    title: "Spot Harmful Additives",
    description: "Get instant alerts on artificial colors, preservatives, and controversial additives.",
    color: "warning" as const,
  },
  {
    icon: Lock,
    title: "100% Private",
    description: "All processing happens on your device. Your food data never leaves your phone.",
    color: "primary" as const,
  },
  {
    icon: Zap,
    title: "Instant Analysis",
    description: "Get comprehensive ingredient breakdown in under 2 seconds.",
    color: "success" as const,
  },
  {
    icon: Heart,
    title: "Personalized Alerts",
    description: "Set your dietary preferences and get warnings tailored to your needs.",
    color: "destructive" as const,
  },
];

const Features = () => {
  return (
    <section className="py-24 bg-muted/30">
      <div className="container px-4">
        <div className="max-w-3xl mx-auto text-center mb-16">
          <span className="inline-block px-4 py-1.5 bg-primary/10 text-primary text-sm font-medium rounded-full mb-6">
            Features
          </span>
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            Everything You Need to
            <br />
            <span className="gradient-text">Eat Smarter</span>
          </h2>
          <p className="text-lg text-muted-foreground">
            Emma goes beyond simple scanning. Get the full picture of what you're really eating.
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {features.map((feature, index) => {
            const colorClasses = {
              primary: "bg-primary/10 text-primary",
              destructive: "bg-destructive/10 text-destructive",
              warning: "bg-warning/10 text-warning",
              success: "bg-success/10 text-success",
            };

            return (
              <div
                key={index}
                className="group p-6 rounded-2xl bg-card border border-border/50 hover:border-primary/30 hover:shadow-lg transition-all duration-300"
              >
                <div className={`w-12 h-12 rounded-xl ${colorClasses[feature.color]} flex items-center justify-center mb-4 group-hover:scale-110 transition-transform`}>
                  <feature.icon className="w-6 h-6" />
                </div>
                <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
                <p className="text-muted-foreground">{feature.description}</p>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Features;

import { Camera, Cpu, FileText } from "lucide-react";

const steps = [
  {
    icon: Camera,
    step: "01",
    title: "Scan Any Label",
    description: "Point your camera at any food label or barcode. Works with products from any country, in any language.",
  },
  {
    icon: Cpu,
    step: "02",
    title: "AI Analyzes",
    description: "Emma's AI processes the ingredients in real-time, identifying sugars, additives, and potential concerns.",
  },
  {
    icon: FileText,
    step: "03",
    title: "Get Clear Results",
    description: "Receive a plain-English breakdown with health scores, warnings, and personalized recommendations.",
  },
];

const HowItWorks = () => {
  return (
    <section className="py-24 bg-muted/30">
      <div className="container px-4">
        <div className="max-w-3xl mx-auto text-center mb-16">
          <span className="inline-block px-4 py-1.5 bg-primary/10 text-primary text-sm font-medium rounded-full mb-6">
            How It Works
          </span>
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            Three Simple Steps to
            <br />
            <span className="gradient-text">Healthier Choices</span>
          </h2>
          <p className="text-lg text-muted-foreground">
            No signup required. Just scan and discover what's really in your food.
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-8 max-w-5xl mx-auto">
          {steps.map((step, index) => (
            <div key={index} className="relative">
              {/* Connector line */}
              {index < steps.length - 1 && (
                <div className="hidden md:block absolute top-12 left-[calc(50%+3rem)] w-[calc(100%-6rem)] h-0.5 bg-gradient-to-r from-primary/50 to-primary/20" />
              )}

              <div className="text-center">
                <div className="relative inline-flex mb-6">
                  <div className="w-24 h-24 rounded-2xl bg-card border border-border shadow-lg flex items-center justify-center">
                    <step.icon className="w-10 h-10 text-primary" />
                  </div>
                  <div className="absolute -top-2 -right-2 w-8 h-8 rounded-full bg-primary text-primary-foreground text-sm font-bold flex items-center justify-center">
                    {step.step}
                  </div>
                </div>
                <h3 className="text-xl font-semibold mb-3">{step.title}</h3>
                <p className="text-muted-foreground">{step.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;

import { Leaf } from "lucide-react";

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <footer className="py-12 bg-muted/50 border-t border-border">
      <div className="container px-4">
        <div className="flex flex-col md:flex-row items-center justify-between gap-6">
          <div className="flex items-center gap-2">
            <div className="w-10 h-10 rounded-xl bg-primary flex items-center justify-center">
              <Leaf className="w-5 h-5 text-primary-foreground" />
            </div>
            <span className="text-xl font-bold">Emma</span>
          </div>

          <nav className="flex flex-wrap items-center justify-center gap-6 text-sm text-muted-foreground">
            <a href="#" className="hover:text-foreground transition-colors">Privacy</a>
            <a href="#" className="hover:text-foreground transition-colors">Terms</a>
            <a href="#" className="hover:text-foreground transition-colors">Contact</a>
            <a href="#" className="hover:text-foreground transition-colors">Blog</a>
          </nav>

          <p className="text-sm text-muted-foreground">
            © {currentYear} Emma. All rights reserved.
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;

